import { Component, OnInit } from '@angular/core';
import { MoviesService } from '../services/movies.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Movies } from 'src/app/shared/interfaces/movies';

@Component({
  selector: 'app-movies-details',
  templateUrl: './movies-details.component.html',
  styleUrls: ['./movies-details.component.css']
})
export class MoviesDetailsComponent implements OnInit {
  selectedMovie: Movies | undefined;
  idActive: number | undefined;

  constructor(
    private activeRouter: ActivatedRoute,
    private movieService: MoviesService,
  ) {}

  ngOnInit() {
    console.log(this.activeRouter.snapshot.params['id']);
    this.idActive = +this.activeRouter.snapshot.params['id'];
    this.movieService.getMovie(this.idActive).subscribe(
      (movie) => {
        this.selectedMovie = movie;
      },
      (error) => {
        console.error('Error fetching movie details:', error);
      }
    );
  }
}
<div class="container mt-4 ps-0" *ngIf="selectedMovie">
    <div class="row mb-3">
        <h4>{{selectedMovie.title}}</h4>
    </div>
    <div class="row">
            <img [src]="'https://image.tmdb.org/t/p/w500' + selectedMovie.poster_path" style="width: 75%; height: 500px; margin: auto;">
    </div>
            <div class="row">
             <p><i class="fa-solid fa-question fa-beat text-warning"></i> {{selectedMovie.overview}}</p>
        </div>
    </div>
